import { Component } from "react";
import { withRouter } from "react-router-dom";

class testBalance extends Component {
  constructor() {
    super();
    this.state = { loading: true };
  }

  async componentDidMount() {
    const res = await fetch(
      `http://localhost:8080/o/c/bankaccounts/42772`
    );
    const json = await res.json();
    this.setState(Object.assign({ loading: false }, json.account[0]));
  }

  render() {
    console.log(this.state);

    if (this.state.loading) {
      return <h2>loading … </h2>;
    }

    const { accountType, accountBalance, accountNumber } = this.state;

    return (
      <div className="details">
        <div>
          <h2>{`${accountType} — ${accountNumber}`}</h2>
          <h2> {accountBalance}</h2>
        </div>
      </div>
    );
  }
}

export default withRouter(testBalance);
